const books = [
  {
    id: 1,
    title: 'Война и мир',
    author: 'Лев Толстой',
    cover: require('@/assets/book-covers/war_and_peace.jpg'), 
    description: 'Роман-эпопея, описывающий русское общество в эпоху войн против Наполеона.',
    year: 1869,
    pages: 1225
  },
  {
    id: 2,
    title: 'Преступление и наказание',
    author: 'Фёдор Достоевский',
    cover: 'https://via.placeholder.com/150x200?text=Преступление+и+наказание',
    description: 'Роман о моральных последствиях преступления, совершённого бедным студентом.',
    year: 1866,
    pages: 672
  },
  {
    id: 3,
    title: 'Мастер и Маргарита',
    author: 'Михаил Булгаков',
    cover: 'https://via.placeholder.com/150x200?text=Мастер+и+Маргарита',
    description: 'Мистический роман, сочетающий сатиру на советскую действительность с философскими размышлениями.',
    year: 1967,
    pages: 384
  }
]

export default {
  getBooks() {
    return books
  },
  getBook(id) {
    return books.find(book => book.id === parseInt(id))
  }
}
